# HungariaReport
class HungariaReport(Report):
		def __init__(self, database, options):
				"""
				Create WebReport object that produces the report.

				The arguments are:

				database        - the GRAMPS database instance
				person          - currently selected person
				options         - instance of the Options class for this report
				"""
				Report.__init__(self, database, options)
				menu = options.menu
				
				self.db = database
				
				self.opts = {}
				
				for optname in menu.get_all_option_names():
					menuopt = menu.get_option_by_name(optname)
					self.opts[optname] = menuopt.get_value()
				
				self.opts['focusname'] = self.db.get_person_from_gramps_id(self.opts['pid']).get_primary_name().get_surname()
				
				self.allpeople = self.sort_handlelist(self.db.get_person_handles(sort_handles=True))
				
				self.got_images = False
				
		# ========================== Utility Functions Begin
		
		# Is a selected language?
		def islang(self, langnum):
				if eval('self.opts[\''+_OPTIONNAME.split('|')[langnum]+'\']'):
						return True
				else:
						return False
		
		# G(et) L(abel) labelname in language langnum.
		def gl(self, labelname, langnum):
				return eval('_LABEL_'+labelname).split('|')[langnum]
		
		def mkdir(self, path, newdir):
				if not os.path.isdir(os.path.join(path, newdir)):
						os.mkdir(os.path.join(path, newdir))
				return os.path.join(path, newdir)
		
		def openfile(self, path, file):
				filename = os.path.join(path, file)
				of = codecs.EncodedFile(open(filename, "w"),'utf-8', 'utf-8','xmlcharrefreplace')
				return of;
		
		def startswithvowel(self, word):
				for letter in _VOWELS:
						if (word[0] == letter):
								return True
				return False
		
		# ========================== Utility Functions Cease
		
		
		
		# ========================== Helper Functions Cease
		
		def make_directories(self):
				
				self.opts['targetpath'] = self.mkdir(self.opts['targetpath'],'HungariaWeb')
				self.mkdir(self.opts['targetpath'],'res')
				self.mkdir(self.opts['targetpath'],'res/css')
				self.mkdir(self.opts['targetpath'],'res/img')
				self.mkdir(self.opts['targetpath'],'res/photos')
				self.mkdir(self.opts['targetpath'],'res/js')
		
		def dump_files(self):
				
				filename = "hungaria.png"
				of = open(os.path.join(self.opts['targetpath'],'res', 'img', filename), "wb")
				data = binascii.a2b_hex(_IMG_SPLASH)
				of.write(data)
				of.close()
				
