		def is_eastern_name_order(self, format):
				marks = 0
				
				if format.find(',') == -1:
						marks = marks + 1
				
				if format.find('surname') > -1:
						surnameidx = format.find('surname')
				
				if format.find('SURNAME') > -1:
						surnameidx = format.find('SURNAME')
				
				if format.find('given') > -1:
						givenidx = format.find('given')
				
				if format.find('GIVEN') > -1:
						givenidx = format.find('GIVEN')
				
				if surnameidx < givenidx:
						marks = marks + 1
				
				if surnameidx > -1:
						marks = marks + 1
				
				if marks == 3:
						return True
				
				return False
		
		def reverse_nameorder(self, name):
				output = ""
				for item in name.split(" "):
					output = item + " " + output
				output = output.replace("  ", " ").replace("  ", " ")
				return output
		
