		def get_parentrelation(self, person, parent):
				
				for fhandle in person.get_parent_family_handle_list():
						family = self.db.get_family_from_handle(fhandle)
						if family.get_father_handle():
								if family.get_father_handle() == parent.handle:
										relation = ""
										for child_ref in family.get_child_ref_list():
												child_handle = child_ref.ref
												if child_handle == person.handle:
														relation = child_ref.get_father_relation()
						if family.get_mother_handle():
								if family.get_mother_handle() == parent.handle:
										relation = ""
										for child_ref in family.get_child_ref_list():
												child_handle = child_ref.ref
												if child_handle == person.handle:
														relation = child_ref.get_mother_relation()
				return str(relation)
		
